import React,{ Component } from 'react'
import Twitter from './Twitter';

class App extends Componet {

  state = {
    loading: false,
    actived:true
  }

  ComponetDidMount() {
    setTimeout(() => {
      this.setState ({
        loading: true
      })
    }, 3000)
  }

  onRemove = () => {
    this.setState({
      actived:false
    })
  }

  render () {
    const posts = [{
      title:'xpto',
      description:'foo'
    },{
      title:'xpto',
      description:'foo'
    }]

    return(
      <div>
          <button onClick={this.onRemove}> component</button>
          {this.state.actived && (
            <Twitter posts={ posts} loading = {this.state.loading} /> )}
      </div>
    )
  }
}

export default App
