import {useState} from 'react';

function SmartCounter(){
   const[quantidy, upQuantidy] = useState(1);

    return (
        <>
            <h1>{quantidy}</h1>
            <button onClick={()=> upQuantidy(quantidy + 1)}>Aumentar</button>
        </>
    )
}

export default SmartCounter;
